dotenv.config();
connectDB();
const app = express();
app.use(express.json());

const port = process.env.PORT || 5000;

app.get("/", (req, res) => {
    res.send("Server is running...");
});

// Example of handling the session option correctly
app.post("/example", (req, res) => {
    const options = req.body.options; // Assume options are sent in the request body
    const session = options && options.session; // Use a safe way to access session
    if (session) {
        // Handle session logic
        res.send("Session is present");
    } else {
        res.send("No session provided");
    }
});

app.listen(port, () => {
    console.log(`Listening on port ${port}`);
});
